package com.endlesscreation.ecsite.service;

import com.endlesscreation.ecsite.dto.MemberResponse;
import com.endlesscreation.ecsite.dto.PageRequest;
import com.endlesscreation.ecsite.dto.ProjectDetailResponse;
import com.endlesscreation.ecsite.dto.SimpleProjectResponse;
import java.text.ParseException;
import java.text.SimpleDateFormat;
import java.util.Arrays;
import java.util.Date;
import java.util.List;
import org.springframework.stereotype.Service;

@Service
public class ProjectService {

    public List<SimpleProjectResponse> findProjects(PageRequest pageRequest) {
        String imageUrl = "data:image/jpeg;base64,";

        SimpleProjectResponse response1 = new SimpleProjectResponse(1L, imageUrl);
        SimpleProjectResponse response2 = new SimpleProjectResponse(2L, imageUrl);

        return Arrays.asList(response1, response2);
    }

    public ProjectDetailResponse findProjectById(Long id) throws ParseException {
        List<String> tags1 = Arrays.asList("해시태그", "Spring", "nodeJS");
        String memberImageUrl = "사람사진 URL";
        MemberResponse member1 = new MemberResponse(1L, "김건훈", "회장", tags1, memberImageUrl);
        MemberResponse member2 = new MemberResponse(2L, "김아무개", "일반", tags1, memberImageUrl);

        String imageUrl = "이미지 URL";

        String startDate = "2020-10-01 00:00:00";
        String endDate = "2020-04-01 00:00:00";
        SimpleDateFormat startDateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");
        SimpleDateFormat endDateFormat = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");

        Date start = startDateFormat.parse(startDate);
        Date end = startDateFormat.parse(endDate);

        return ProjectDetailResponse.builder()
            .id(id)
            .imageUrl(imageUrl)
            .description("작품설명 작품설명 작품설명 작품설명 작품설명 작품설명 작품설명 작품설명 작품설명 작품설명작품설명 작품설명 작품설명 작품설명 작품설명")
            .participants(Arrays.asList(member1, member2))
            .affiliatedEvent("EC 어드벤스")
            .startDate(start)
            .endDate(end)
            .techStack(Arrays.asList("java", "spring", "typescript", "react", "jpa", "mysql"))
            .presentationUrl("발표자료 URL")
            .githubUrl("깃헙 URL")
            .build();
    }
}
